import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, IconButton, Popover } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { useNavigate } from 'react-router-dom';

const MovieCard = ({ movie }) => {
  const [anchorEl, setAnchorEl] = useState(null);
  const navigate = useNavigate();

  const { name, premiered, image, genres, language, summary } = movie.show;

  const handleClick = () => {
    // Redirect to the movie details page
    navigate('/movie-details');
  };

  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const open = Boolean(anchorEl);

  return (
    <Button
      onClick={handleClick}
      onMouseEnter={handlePopoverOpen}
      onMouseLeave={handlePopoverClose}
      sx={{ textAlign: 'left', width: '100%' }}
    >
      <Card>
        <CardContent>
          <img src={image.medium} alt={name} />
          <Typography variant="h5" component="div">
            {name}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {`${premiered.slice(0, 4)} | ${language} | ${genres.join(', ')}`}
          </Typography>
        </CardContent>
        <Popover
          open={open}
          anchorEl={anchorEl}
          onClose={handlePopoverClose}
          anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'left',
          }}
          transformOrigin={{
            vertical: 'top',
            horizontal: 'left',
          }}
        >
          <CardContent>
            <IconButton
              sx={{ position: 'absolute', top: 8, right: 8, color: 'primary.main' }}
              onClick={(event) => {
                event.stopPropagation();
              }}
            >
              <ShoppingCartIcon />
            </IconButton>
            <Typography variant="body2" color="text.secondary">
              {summary}
            </Typography>
          </CardContent>
        </Popover>
      </Card>
    </Button>
  );
};

export default MovieCard;
