import React from 'react';
import { Card, CardContent, CardMedia, Typography, Grid } from '@mui/material';

const movies = [
  {
    name: 'Movie 1',
    year: 2020,
    runtime: 120,
    imageUrl: 'https://static.tvmaze.com/uploads/images/medium_portrait/425/1064746.jpg',
  },
  {
    name: 'Movie 2',
    year: 2019,
    runtime: 105,
    imageUrl: 'https://example.com/movie2.jpg',
  },
  // Add more movie objects as needed
];

const MovieRow = () => {
  return (
    <Grid container spacing={2}>
      {movies.map((movie, index) => (
        <Grid item xs={6} sm={4} md={3} key={index}>
          <Card>
            <CardMedia component="img" height="140" image={movie.imageUrl} alt={movie.name} />
            <CardContent>
              <Typography variant="h6">{movie.name}</Typography>
              <Typography variant="body2" color="textSecondary">
                Year: {movie.year} | Runtime: {movie.runtime} mins
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default MovieRow;
