import React, { useState } from 'react';
import { Card, CardContent, Typography, Button, IconButton, Popover, Box } from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { useNavigate } from 'react-router-dom';

const MovieCard = ({ movie }) => {
  const [isHovered, setIsHovered] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const navigate = useNavigate();

  const { name, premiered, image, genres, language, summary } = movie.show;

  const handleClick = () => {
    // Redirect to the movie details page
    navigate('/movie-details');
  };

  const handlePopoverOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handlePopoverClose = () => {
    setAnchorEl(null);
  };

  const isPopoverOpen = Boolean(anchorEl);

  return (
    <>
      <Button
        onClick={handleClick}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        sx={{ textAlign: 'left', width: '100%' }}
      >
        <Card>
          <CardContent>
            <img src={image.medium} alt={name} />
            <Typography variant="h5" component="div">
              {name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {`${premiered.slice(0, 4)} | ${language} | ${genres.join(', ')}`}
            </Typography>
          </CardContent>
        </Card>
      </Button>
      <Popover
        open={isHovered && isPopoverOpen}
        anchorEl={anchorEl}
        onClose={handlePopoverClose}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        transformOrigin={{
          vertical: 'top',
          horizontal: 'center',
        }}
      >
        <Box sx={{ p: 2 }}>
          <Typography variant="body2" color="text.secondary">
            {summary}
          </Typography>
          <IconButton
            sx={{ position: 'absolute', top: 8, right: 8, color: 'primary.main' }}
            onClick={(event) => {
              event.stopPropagation();
            }}
          >
            <ShoppingCartIcon />
          </IconButton>
        </Box>
      </Popover>
    </>
  );
};

export default MovieCard;
